package chap03;

public class hwrk3_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=25;
		System.out.println("a==b : "+(a==b));
		System.out.println("a!=b : "+(a!=b));
		System.out.println("a>b : "+(a>b));
		System.out.println("a<b : "+(a<b));
		System.out.println("b>=a : "+(b>=a));
		System.out.println("b<=a : "+(b<=a));

	}

}
