package hwrk;

public class hwrk23 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10, y=20;
		
		System.out.println("x와 y의 값을 확인합니다.");
		System.out.println("x값: "+x);
		System.out.println("y값: "+y);
		
		x=20;
		y=10;
		
		System.out.println("x와 y의 값을 변경합니다.");
		System.out.println("x값: "+x);
		System.out.println("y값: "+y);

	}

}
