package hwrk;

public class hwrk2_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println('\''); // ' 출력
		System.out.println("abc\t123\b456"); // abc 탭 123 백스페이스 456 출력
		System.out.println('\n'); // 줄바꿈
		System.out.println("\"Hello\""); // "Hello" 출력
		System.out.println("c:\\"); // c:\\ 출력
	}

}
