package hwrk;

public class hwrk3_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k01_year=0; // k01_year은 0의 값을 가짐
		int k01_age=14; // k01_age는 14의 값을 가짐
		
		System.out.println("결과1: "+k01_year); // 결과1은 0을 출력
		System.out.println("결과2: "+k01_age); // 결과2는 14를 출력
		
		k01_year=k01_age+2000; // k01_year은 14+2000=2014의 값을 가짐
		k01_age=k01_age+1; // ㅏ01_age는 14+1=15의 값을 가짐
		
		System.out.println("결과3: "+k01_year); // 결과3은 2014를 출력
		System.out.println("결과4: "+k01_age); // 결과 4는 15를 출력
		

	}

}
