package hwrk;

public class hwrk3_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println((char)65); // 아스키코드에서 65는 'A'를 의미
		
		System.out.println((int)'A'); // 아스키코드에서 문자 A는 65
		
		System.out.println((int)1.6f); // 실수 1.6을 정수로 표현 -> 출력값은 1
		
		System.out.println((float)10); // 정수 10을 실수로 표현 -> 출력값은 10.0

	}

}
