package practice;

public class prac4_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<=10; i++) {
			for(int j=0; j<10-i; j++) {
				System.out.print(" ");}
			for(int k=1; k<=(2*i)+1; k++) {
				System.out.print("*");
				}
			System.out.println("");
		}
		
		

	}

}
